from django.conf.urls import patterns, urlfrom book import viewsurlpatterns = patterns('',    url(r'^$', views.index, name='index'),    url(r'^addauthor/$', views.addAuthor, name='addAuthor'),    url(r'^authors/$', views.authors, name='authorlist'),    url(r'^authors/(?P<page>[0-9]+)?/$', views.authors, name='authors'),    url(r'^editauthor/(?P<pk>[0-9]+)/$', views.addAuthor, name='editAuthor'),    url(r'^book/add/$', views.editBook, name='addBook'),    url(r'^book/edit/(?P<pk>[0-9]+)/$', views.editBook, name='editBook'),    url(r'^book/detail/(?P<pk>[0-9]+)/$', views.detailBook, name='detailBook'),    url(r'^book/comment/(?P<pk>[0-9]+)/$', views.commentBook, name='commentBook'),    url(r'^book/deleteComment/(?P<pk>[0-9]+)/(?P<bpk>[0-9]+)/$', views.deleteComment, name='deleteComment'),    url(r'^book/rate/(?P<pk>[0-9]+)/$', views.rateBook, name='rateBook'),    url(r'^book/own/(?P<pk>[0-9]+)/$', views.ownBook, name='ownBook'),    url(r'^books/$', views.books, name='booklist'),    url(r'^books/(?P<page>[0-9]+)?/$', views.books, name='books'),    url(r'^books/(?P<order>[a-z]+)?/$', views.setOrderBooks,  name='booksrating'),    url(r'^booksearch/$', views.setBookSearch,  name='booksearch'),    url(r'^booksfilter/$', views.setBookFilter,  name='booksfilter'),    url(r'^books/category/add$', views.addBookCategory,  name='book_category_add'),    url(r'^books/author/add$', views.addBookAuthor,  name='book_author_add'),)